$_MODULE['<{layerslider}prestashop>_00cf981f6a6ebc3d812b2c9d49e71891'] = 'Use relative URLs for local images. This setting could be important when moving your PS installation.';
$_MODULE['<{layerslider}prestashop>_c878cdf0cb53e7dbeb791eab13b84146'] = 'Allow restarting slides on resize';
$_MODULE['<{layerslider}prestashop>_0296ddaa31acdc8e379a3de8412aff9a'] = 'Certain transformation and transition options cannot be updated on the fly when the browser size or device orientation changes. By enabling this option, the slider will automatically detect such situations and will restart the itself to preserve its appearance.';
$_MODULE['<{layerslider}prestashop>_d9f1451d7e147a9d76732b903c85fd98'] = 'Use srcset attribute';
$_MODULE['<{layerslider}prestashop>_d962a6678a6f57c0697bdba39433e0d7'] = 'The srcset attribute allows loading dynamically scaled images based on screen resolution. It can save bandwidth and allow using retina-ready images on high resolution devices. In some rare edge cases, this option might cause blurry images.';
$_MODULE['<{layerslider}prestashop>_451af3f11142c7626d839eb7674d3e0a'] = 'Prefer Blend Mode';
$_MODULE['<{layerslider}prestashop>_6da524b45307ee7baa37799db0cd4796'] = 'Enable this option to avoid blend mode issues with slide transitions. Due to technical limitations, this will also clip your slide transitions regardless of your settings.';
$_MODULE['<{layerslider}prestashop>_5e1575919121f7253b56bb39b99e1042'] = 'YourLogo';
$_MODULE['<{layerslider}prestashop>_367a210257e2ea49914c4a695f8abf70'] = 'A fixed image layer can be shown above the slider that remains still throughout the whole slider. Can be used to display logos or watermarks.';
