$_MODULE['<{layerslider}prestashop>_13eac5a039bc8041315fe1165a245a98'] = 'The size of the slide background image. Leave this option on inherit if you want to set it globally from Slider Settings.';
$_MODULE['<{layerslider}prestashop>_c49484faf79e2388b580e49444d6ac11'] = 'Inherit';
