$_MODULE['<{layerslider}prestashop>_cb5feb1b7314637725a2e73bdc9f7295'] = 'Color';
$_MODULE['<{layerslider}prestashop>_9880a2532d7550cb4bef6c24ce2275f3'] = 'The slide background color. You can use color names, hexadecimal, RGB or RGBA values.';
$_MODULE['<{layerslider}prestashop>_9d8cfafeba857023956c26437b6570fc'] = 'Set a slide thumbnail';
$_MODULE['<{layerslider}prestashop>_ee3a294033bb8e5d1a208d3abef859b3'] = 'The thumbnail image of this slide. Click on the image to open the Media Library to choose or upload an image. If you leave this field empty, the slide image will be used.';
$_MODULE['<{layerslider}prestashop>_e02d2ae03de9d493df2b6b2d2813d302'] = 'Duration';
$_MODULE['<{layerslider}prestashop>_37657a348667aa7ecac5e23b51c46fc7'] = 'Here you can set the time interval between slide changes, this slide will stay visible for the time specified here. This value is in millisecs, so the value 1000 means 1 second. Please don’t use 0 or very low values.';
$_MODULE['<{layerslider}prestashop>_50f4f41e03ca0aab5e91568d352c82fd'] = 'Origami';
$_MODULE['<{layerslider}prestashop>_af66fa757854d80db87737e2a4b8844b'] = 'We’ve made our pre-defined slide transitions with special care to fit in most use cases. However, if you would like to increase or decrease the speed of these transitions, you can override their timing here by providing your own transition length in milliseconds. (1 second = 1000 milliseconds)';
$_MODULE['<{layerslider}prestashop>_38912a644194123a9ee9d01e126fcfae'] = 'custom duration';
$_MODULE['<{layerslider}prestashop>_1605a4f4a4e45503d3ac914de9fba80a'] = 'Time Shift';
$_MODULE['<{layerslider}prestashop>_6d106138f5fadb601a337fca4d4b7a2e'] = 'You can shift the starting point of the slide animation timeline, so layers can animate in an earlier time after a slide change. This value is in milliseconds. A second is 1000 milliseconds. You can only use a negative value.';
$_MODULE['<{layerslider}prestashop>_e1f70f4a4265cb51c1b219aa60eaa441'] = 'Enter URL';
$_MODULE['<{layerslider}prestashop>_dce5ef7bcb8f68129455e14e1c7cd9cb'] = 'If you want to link the whole slide, type the URL here. You can choose one of the pre-defined options from the dropdown list when you click into this field. You can also type a hash mark followed by a number to link this layer to another slide. Example: #3 - this will switch to the third slide.';
$_MODULE['<{layerslider}prestashop>_1f7412ef1f7fdba3cc269af9a0b7a40c'] = 'Link Target';
$_MODULE['<{layerslider}prestashop>_e82688575274fd1ba522f5044431cc93'] = 'Scroll to element (Enter selector)';
$_MODULE['<{layerslider}prestashop>_1fc71d8275be86e2787bc4a8f0d62c84'] = 'Choose whether the slide link should be on top or underneath your layers. The later option makes the link clickable only at empty spaces where the slide background is visible, and enables you to link both slides and layers independently from each other.';
$_MODULE['<{layerslider}prestashop>_7e04fe64ab136e31d62fcc6ea113f1ca'] = 'On top of layers';
$_MODULE['<{layerslider}prestashop>_d722a275a3f48eb46d892c9df863e1af'] = 'Underneath layers';
$_MODULE['<{layerslider}prestashop>_739cea2b85156183f917f2ccaa10a50c'] = '#ID';
$_MODULE['<{layerslider}prestashop>_06f695600f91bfbd579d9d8f5a0147bd'] = 'You can apply an ID attribute on the HTML element of this slide to work with it in your custom CSS or Javascript code.';
$_MODULE['<{layerslider}prestashop>_550dc007dba485adc22eddfe0af40a81'] = 'Deeplink';
$_MODULE['<{layerslider}prestashop>_2370ab575d5bcb31286546ba6f3a84db'] = 'You can specify a slide alias name which you can use in your URLs with a hash mark, so Creative Slider will start with the correspondig slide.';
$_MODULE['<{layerslider}prestashop>_436e4906cf91bb911a38eb161e858a69'] = 'Global Hover';
$_MODULE['<{layerslider}prestashop>_f22bc2f96ed30d26a29060598c44c2fa'] = 'By turning this option on, all layers will trigger their Hover Transitions at the same time when you hover over the slider with your mouse cursor. It’s useful to create spectacular effects that involve multiple layer transitions and activate on hovering over the slider instead of individual layers.';
$_MODULE['<{layerslider}prestashop>_7acdf85c69cc3c5305456a293524386e'] = 'Hidden';
$_MODULE['<{layerslider}prestashop>_e32d5c1ab7aef83217ea462806ea062c'] = 'If you don’t want to use this slide in your front-page, but you want to keep it, you can hide it with this switch.';
$_MODULE['<{layerslider}prestashop>_d33fd48027b557f5bcb3f15a238d7537'] = 'Overflow layers';
$_MODULE['<{layerslider}prestashop>_5bbae9c02b64de11da4d22707a297710'] = 'By default the slider clips the layers outside of its bounds. Enable this option to allow overflowing content.';
$_MODULE['<{layerslider}prestashop>_4252b72e6ebcd4d4b4c2e46a786f03d2'] = 'Zoom';
$_MODULE['<{layerslider}prestashop>_f5ca4abce85e2dddb0342d0bae3a7270'] = 'Zoom In';
$_MODULE['<{layerslider}prestashop>_30850b501f98539b1aabaa29fabe41ef'] = 'Zoom Out';
$_MODULE['<{layerslider}prestashop>_8d2de5368588552fbae54044ac5c7b3d'] = 'Rotate';
$_MODULE['<{layerslider}prestashop>_8aa2fa2e684ba408f551f3504c47427e'] = 'The amount of rotation (if any) in degrees used in the Ken Burns effect. Negative values are allowed for counterclockwise rotation.';
$_MODULE['<{layerslider}prestashop>_2ed8e1d37be922f23b2713c3e69a85ae'] = 'Increase or decrease the size of the slide background image in the Ken Burns effect. The default value is 1, the value 2 will double the image, while 0.5 results half the size. Negative values will flip the image.';
$_MODULE['<{layerslider}prestashop>_a1fa27779242b4902f7ae3bdd5c6d508'] = 'Type';
$_MODULE['<{layerslider}prestashop>_4a2d97fd5296d09fea3cbe45d107a7b3'] = 'The default value for parallax layers on this slide, which they will inherit, unless you set it otherwise on the affected layers.';
$_MODULE['<{layerslider}prestashop>_8c94134f25efe7680f9743b2774e8e1f'] = '2D';
$_MODULE['<{layerslider}prestashop>_76aa96369abbba52e621bfa83da8e64f'] = '3D';
$_MODULE['<{layerslider}prestashop>_a4ecfc70574394990cf17bd83df499f7'] = 'Event';
$_MODULE['<{layerslider}prestashop>_87932092d26d18e63889036bbf70518e'] = 'You can trigger the parallax effect by either scrolling the page, or by moving your mouse cursor / tilting your mobile device. This is the default value on this slide, which parallax layers will inherit, unless you set it otherwise directly on them.';
$_MODULE['<{layerslider}prestashop>_58af070b9f7f3dc090af0ece708e8869'] = 'Cursor or Tilt';
$_MODULE['<{layerslider}prestashop>_61ca8fa7a7c8b4699523bc8773c306d0'] = 'Axes';
$_MODULE['<{layerslider}prestashop>_fc888362a84b5b975136bbca8e1fc5c6'] = 'Choose on which axes parallax layers should move. This is the default value on this slide, which parallax layers will inherit, unless you set it otherwise directly on them.';
$_MODULE['<{layerslider}prestashop>_6adf97f83acf6453d4a6a4b1070f3754'] = 'None';
$_MODULE['<{layerslider}prestashop>_2c452204ed58a6f4a3bcda80344b338a'] = 'Both axes';
$_MODULE['<{layerslider}prestashop>_ad09aaae9c9ffe403ffdb2a6568f5a36'] = 'Horizontal only';
$_MODULE['<{layerslider}prestashop>_354908b89248f3dcbab970b32860a331'] = 'Vertical only';
$_MODULE['<{layerslider}prestashop>_8b622b0a6ecf4e0116eaf248876f8700'] = 'Transform Origin';
$_MODULE['<{layerslider}prestashop>_e5a904ff73268b65730e4fb8b6127bf1'] = 'Sets a point on canvas from which transformations are calculated. For example, a layer may rotate around its center axis or a completely custom point, such as one of its corners. The three values represent the X, Y and Z axes in 3D space. Apart from the pixel and percentage values, you can also use the following constants: top, right, bottom, left, center.';
$_MODULE['<{layerslider}prestashop>_97289d779de759c4fe3a0ef9d3a4ff3d'] = 'Move duration';
$_MODULE['<{layerslider}prestashop>_ef82e470cae5696da7dc47e9a50fa8f7'] = 'Controls the speed of animating layers when you move your mouse cursor or tilt your mobile device. This is the default value on this slide, which parallax layers will inherit, unless you set it otherwise directly on them.';
$_MODULE['<{layerslider}prestashop>_0bdd8970e06688e4a6ad694b8ed7b9f7'] = 'Leave duration';
$_MODULE['<{layerslider}prestashop>_9aa9b8945ad7ff74e04fb53dbbe3d1f2'] = 'Controls how quickly your layers revert to their original position when you move your mouse cursor outside of a parallax slider. This value is in milliseconds. 1 second = 1000 milliseconds. This is the default value on this slide, which parallax layers will inherit, unless you set it otherwise directly on them.';
$_MODULE['<{layerslider}prestashop>_0aa6f4210bf373c95eda00232e93cd98'] = 'Distance';
$_MODULE['<{layerslider}prestashop>_396950a091a406fcd6f9f4cc75927536'] = 'Increase or decrease the amount of layer movement when moving your mouse cursor or tilting on a mobile device. This is the default value on this slide, which parallax layers will inherit, unless you set it otherwise directly on them.';
$_MODULE['<{layerslider}prestashop>_f1a42bd417390fc63b030a519624607a'] = 'Rotation';
$_MODULE['<{layerslider}prestashop>_331ee65576c7333ba77a13d691219105'] = 'Increase or decrease the amount of layer rotation in the 3D space when moving your mouse cursor or tilting on a mobile device. This is the default value on this slide, which parallax layers will inherit, unless you set it otherwise directly on them.';
$_MODULE['<{layerslider}prestashop>_a80420eef88d11f77532f1b9cb467fa3'] = 'Perspective';
$_MODULE['<{layerslider}prestashop>_7ba7448042bdf18c7669bec909a0a7be'] = 'Changes the perspective of layers in the 3D space. This is the default value on this slide, which parallax layers will inherit, unless you set it otherwise directly on them.';
$_MODULE['<{layerslider}prestashop>_05b1dc77590105a18fc4c02526fe0c7a'] = 'Autoplay';
$_MODULE['<{layerslider}prestashop>_2b3de50de6ab7bd0aaf46ae654032790'] = 'Show Info';
$_MODULE['<{layerslider}prestashop>_24b6472fcfa59cddd3f7bbc326582b5f'] = 'Controls';
$_MODULE['<{layerslider}prestashop>_58bccbc47e6de6783fb90fd62890a171'] = 'Fill mode';
$_MODULE['<{layerslider}prestashop>_bd7a9717d29c5ddcab1bc175eda1e298'] = 'Volume';
$_MODULE['<{layerslider}prestashop>_56a7f02a3b02e4a16864670ff48745e1'] = 'Use this video as slide background';
$_MODULE['<{layerslider}prestashop>_4ee3787bc7df95d25272524868e60d4d'] = 'Forces this layer to act like the slide background by covering the whole slider and ignoring some transitions. Please make sure to provide your own poster image with the option above, so the slider can display it immediately on page load.';
$_MODULE['<{layerslider}prestashop>_9d16dd63ee0afa947dc998fb3d5a4a96'] = 'Choose an overlay image:';
$_MODULE['<{layerslider}prestashop>_e13b4cc51565c9030f943e31c253c8aa'] = 'Cover your videos with an overlay image to have dotted or striped effects on them.';
$_MODULE['<{layerslider}prestashop>_1529fb44e04df26ff7666faa9f58c605'] = 'OffsetX';
$_MODULE['<{layerslider}prestashop>_115e98e8389c322734610e1f4b8c201d'] = 'Shifts the layer starting position from its original on the horizontal axis with the given number of pixels. Use negative values for the opposite direction. Percentage values are relative to the width of this layer. The values “left” or “right” position the layer out the staging area, so it enters the scene from either side when animating to its destination location.';
$_MODULE['<{layerslider}prestashop>_1508a7d829d65cf5e117fdc2aca0f155'] = 'OffsetY';
$_MODULE['<{layerslider}prestashop>_49a9357337339c0925563236288f9921'] = 'Shifts the layer starting position from its original on the vertical axis with the given number of pixels. Use negative values for the opposite direction. Percentage values are relative to the height of this layer. The values “top” or “bottom” position the layer out the staging area, so it enters the scene from either vertical side when animating to its destination location.';
$_MODULE['<{layerslider}prestashop>_7686cf30c00c9f146a9224a33ba99a0f'] = 'The length of the transition in milliseconds when the layer enters the scene. A second equals to 1000 milliseconds.';
$_MODULE['<{layerslider}prestashop>_f2572c86c0aff2b2b7a92fbb6dee1491'] = 'Start at';
$_MODULE['<{layerslider}prestashop>_1f413844a6a15351c7a32a16ce962bfd'] = 'Delays the transition with the given amount of milliseconds before the layer enters the scene. A second equals to 1000 milliseconds.';
$_MODULE['<{layerslider}prestashop>_32485d52ad7aea25b3bc80d416e76a3b'] = 'Easing';
$_MODULE['<{layerslider}prestashop>_ca9a3cf0ea2e65cd611fe23633cd1186'] = 'The timing function of the animation. With this function you can manipulate the movement of the animated object. Please click on the link next to this select field to open easings.net for more information and real-time examples.';
$_MODULE['<{layerslider}prestashop>_ecdc93d174113fe69b7cbf2c42b5a1cb'] = 'Fade the layer during the transition.';
$_MODULE['<{layerslider}prestashop>_b98c4145f7f66383ce71bf31bc4850b2'] = 'Rotates the layer by the given number of degrees. Negative values are allowed for counterclockwise rotation.';
$_MODULE['<{layerslider}prestashop>_801e0dd823542f625e0f2ee135efb62d'] = 'RotateX';
$_MODULE['<{layerslider}prestashop>_54f64742da0d7bd157ccaf54d4ac73a1'] = 'Rotates the layer along the X (horizontal) axis by the given number of degrees. Negative values are allowed for reverse direction.';
$_MODULE['<{layerslider}prestashop>_ea0928ab345567847020e1544892fba0'] = 'RotateY';
$_MODULE['<{layerslider}prestashop>_209eea8f9d8a60293098e07e8951e156'] = 'Rotates the layer along the Y (vertical) axis by the given number of degrees. Negative values are allowed for reverse direction.';
$_MODULE['<{layerslider}prestashop>_5a013b463ee01be2924568775fdddfb1'] = 'SkewX';
$_MODULE['<{layerslider}prestashop>_253108f96bfad9912bb2178a4c7aef73'] = 'Skews the layer along the X (horizontal) by the given number of degrees. Negative values are allowed for reverse direction.';
$_MODULE['<{layerslider}prestashop>_eeb66258f8bf4bb3c0763461b5c1797d'] = 'SkewY';
$_MODULE['<{layerslider}prestashop>_9d19b1698a8b2f7b0efa8c91067053dd'] = 'Skews the layer along the Y (vertical) by the given number of degrees. Negative values are allowed for reverse direction.';
$_MODULE['<{layerslider}prestashop>_b5e0c179725e7f282ffb8e6a96797b49'] = 'ScaleX';
$_MODULE['<{layerslider}prestashop>_15cfa2ea26e51b8489ace013f1b2ec5c'] = 'Scales the layer along the X (horizontal) axis by the specified vector. Use the value 1 for the original size. The value 2 will double, while 0.5 shrinks the layer compared to its original size.';
