$_MODULE['<{layerslider}prestashop>_44607dfb557d61bef9b92a63c18c49b2'] = 'This is the most commonly used method. Just copy the shortcode above and paste it into the product/category/cms page editor, then it will automatically be replaced on your front-end sites with the actual slider. Most page builder solutions like Visual Composer or theme integrated ones usual also have support to insert custom shortcodes.';
$_MODULE['<{layerslider}prestashop>_d59048f21fd887ad520398ce677be586'] = 'Learn more';
