$_MODULE['<{layerslider}prestashop>_80cad31ea3ca20b01f22759a3e322fbb'] = 'Custom 2D transitions';
$_MODULE['<{layerslider}prestashop>_392d8a76e64b07d5159fdc287b81157d'] = 'You haven’t created any custom 2D transitions yet.';
$_MODULE['<{layerslider}prestashop>_1299e945daeb9ccd80a841a117ca76dc'] = 'Custom 3D transitions';
$_MODULE['<{layerslider}prestashop>_8d4f1873d2c19321b43ca227764e66ca'] = 'You haven’t created any custom 3D transitions yet.';
$_MODULE['<{layerslider}prestashop>_666834c9cec98a3d71f96489951cc261'] = 'Special effects are like regular slide transitions and they work in the same way. You can set them on each slide individually. Mixing them with other transitions on other slides is perfectly fine. You can also apply them on all of your slides at once by pressing the “Apply to others” button above. In case of 3D special effects, selecting additional 2D transitions can ensure backward compatibility for older browsers.';
$_MODULE['<{layerslider}prestashop>_b8855caaf641a9f8cd794b263fd3187f'] = 'Origami transition';
$_MODULE['<{layerslider}prestashop>_120a5b58df4cf329020874fee6e1a01c'] = 'Share your gorgeous photos with the world or your loved ones in a truly inspirational way and create sliders with stunning effects with Origami.';
$_MODULE['<{layerslider}prestashop>_2b0cda1ac8369a2372e2de48907dabc4'] = 'Origami is a form of 3D transition and it works in the same way as regular slide transitions do. Besides Internet Explorer, Origami works in all the modern browsers (including Edge).';
$_MODULE['<{layerslider}prestashop>_d8051901953ab9ec06e0bd5e929da146'] = 'Use it on this slide';
$_MODULE['<{layerslider}prestashop>_b035362ed402e3fdf8d1683b06187fef'] = 'Click here for live example';
$_MODULE['<{layerslider}prestashop>_058620f1ca65c7045f200a7587d0c87e'] = 'More effects are coming soon';
$_MODULE['<{layerslider}prestashop>_d1673e2c8908f8939c5d0ba949ebc80b'] = 'Import Sliders';
$_MODULE['<{layerslider}prestashop>_5ed18aa2ff3d637f7b743a20c913047b'] = 'Here you can upload your previously exported sliders. To import them to your site, you just need to choose and select the appropriate export file (files with .zip or .json extensions), then press the Import Sliders button.';
$_MODULE['<{layerslider}prestashop>_d69de1175ad3d34048bfd72f8c2728ea'] = 'Looking for the importable demo content? Check out the %sTemplate Store%s.';
$_MODULE['<{layerslider}prestashop>_cc0da7a91d632e541ca11db8e8e77c18'] = 'Notice: In order to import from outdated versions (pre-v3.0.0), you need to create a new file and paste the export code into it. The file needs to have a .json extension, then you will be able to upload it.';
$_MODULE['<{layerslider}prestashop>_6a8995445835199a5a2989af78cf314b'] = 'Welcome to Creative Slider 6';
$_MODULE['<{layerslider}prestashop>_06e54afd2dadeae4acf6b4ee1c062576'] = 'Thank you for installing Creative Slider! Version 6 is our biggest update yet.';
$_MODULE['<{layerslider}prestashop>_dc9f785a6f284e6463c2be4e42907a82'] = 'Let’s Get Started';
$_MODULE['<{layerslider}prestashop>_5fef629b836b60819e0c0ce49cd2b9d7'] = 'What’s New';
$_MODULE['<{layerslider}prestashop>_5272611948606d1985bb29e839546ad6'] = 'First steps';
$_MODULE['<{layerslider}prestashop>_ddcf50c29294d4414f3f7c1bbc892cb5'] = 'Resources';
$_MODULE['<{layerslider}prestashop>_5df670f68eb9fad7449849426ed061ff'] = 'The newest key features introduced in major updates';
$_MODULE['<{layerslider}prestashop>_4e95a3676f86ba71768cbed29be9a570'] = 'Faster, Better, Prettier';
$_MODULE['<{layerslider}prestashop>_abad5ec32b0afbbfcf238807d8c1f958'] = 'Both the admin interface and front-end sliders load faster and perform better. This release has numerous bug fixes and improvements. So much actually, that we can’t even write a complete list. The admin interface has also been redesigned a bit to provide familiar but much cleaner and easier controls.';
$_MODULE['<{layerslider}prestashop>_de39ab6fbf735911b573a5768dc003d6'] = 'Reworked Responsive Mode, New Slider Layouts, Device Dependent Layouts';
$_MODULE['<{layerslider}prestashop>_900d41a0dd8849b16a32078d54f4d31d'] = 'The new responsive mode performs significantly better and in most cases it does not require any special configuration. Creative Slider now also have multiple new layout modes like full size, hero screne and full screen sliders. These will allow new use cases such as running slideshows on billboards. It’s also possible to make different layouts for different devices (phones, tables, desktop).';
$_MODULE['<{layerslider}prestashop>_986afbbb0b02a5230ab4a59a2c96644c'] = 'New Transitions, Text Animations, Loop, Hover, Filters and a lot more';
$_MODULE['<{layerslider}prestashop>_ac55082e2a47a3a6b9e1ef9a5234be62'] = 'We’ve added lots of new transition options, which greatly extends the capabilities of your slideshows. You can now create chainable multi-step animations enhanced with stylish text transitions and interactive hover animations. There’s also hidden surprises like the much requested Ken Burns effect, 3D parallax effect, and our new “play by scroll” feature. Oh, and don’t forget to try out filters, which can be used to apply blur, sepia, grayscale etc. effects to your layers among many other.';
$_MODULE['<{layerslider}prestashop>_6efa9736b46a2068585557de18d8e386'] = 'Static Layers';
$_MODULE['<{layerslider}prestashop>_d79169b8ff3c035ae56b21132b06ab3e'] = 'Layers can now enter and leave on any slide. You can easily build more complex animations where layers persist across multiple slides. Or just use it as an “always on top” feature.';
$_MODULE['<{layerslider}prestashop>_030df94c069da621788178ab35da71e7'] = 'Undo and Redo';
$_MODULE['<{layerslider}prestashop>_2d73a4e9dad62bb0fd89683012ce4ec3'] = 'Web apps usually don’t have controls to undo unwanted/accidental changes. Creative Slider now has. And it’s even preserved for later use, whenever you need it.';
$_MODULE['<{layerslider}prestashop>_ebb1ba058c25761d0b79fe55b8384da7'] = 'Multi-selection, Resize, Rulers + Guides';
$_MODULE['<{layerslider}prestashop>_9984a8efc476bfc61abf7acfee770b7d'] = 'Do you ever wanted to move around multiple layers and once? Creative Slider 6 now supports multiple selection, which allows you altering groups of layers seamlessly. With rulers and keyboard shortcuts, you can now move/resize/etc your layers with precision. There’s also Guides and a snap to feature built in.';
$_MODULE['<{layerslider}prestashop>_07e85dd743c9622e4af60ff70977bbac'] = 'Keyboard shortcuts &amp; Popout Editor';
$_MODULE['<{layerslider}prestashop>_6be0307097aaeb1787624b56954dc115'] = 'The slider builder interface can now be controlled by keyboard shortcuts. These shortcuts also introduces a lot of new features, like editing layers content right in the preview area or copying and pasting layers across slides and sliders. We’ve also introduced the Popout Editor, which transforms the layer options area into a floating window that can be resized and placed wherever you want. This makes it extremely easy to edit everything in one place, without the need to scroll up and down constantly.';
$_MODULE['<{layerslider}prestashop>_f62ef472ce6cd891d4dfd0dc0ed926f7'] = 'Image Editor';
$_MODULE['<{layerslider}prestashop>_32efb8bb21b3fb1a932d7fe3aa0e8961'] = 'With the help of Adobe’s Creative SDK, Creative Slider 6 now has a built-in image editor. You can perform common tasks like resizing, cropping, rotating images, as well as photo retouching, adding frames, text, effects, stickers and a lot more. It’s like a mini-Photoshop.';
$_MODULE['<{layerslider}prestashop>_b71d084d6b26f408b5d4a508137a41f3'] = 'And a lot more ...';
$_MODULE['<{layerslider}prestashop>_411598f063c5106d99162e95f7b1222a'] = 'There’s way more new features and improvement that we can list here: scheduled sliders, retina-ready, serving dynamically scaled images to reduce data usage and speed up page loads on mobile devices, redesigned transition builder with import feature, continuous live previews, new interactive timeline, reworked multimedia layer support with custom video poster and controls, custom HTML attributes, drag ’n’ drop image upload, smart links, copy & paste layer styles and transition settings, named slides, new slider/slide background settings, auto-fit zoom, align layers feature. <br><br>The number of new features almost never end, but this list must. :)';
$_MODULE['<{layerslider}prestashop>_a480de612ba4d2c2c3e446cb5a876be0'] = 'The future of Creative Slider';
$_MODULE['<{layerslider}prestashop>_f07574172be801ab3ca874434d4eac75'] = 'Creative Slider 6 is just the beginning of our awesome new ideas. One of the main goals was to completely rebuild the module architecture and modernize its core. This allows us to implement new features way more faster and improve upon existing functionality with an easily maintainable code base. Beyond the predictable feature updates like the complete overhaul of skins, dynamic contents, etc (which will arrive very soon); we already have an experimental offline slider editor and preliminary support for Creative Slider extensions. This later one will soon be available for anyone to extend the modules capabilities.';
$_MODULE['<{layerslider}prestashop>_cf828a8dcfccdf5f7c51e8076cf15052'] = 'Considerations before starting to use the module';
$_MODULE['<{layerslider}prestashop>_24e1ed887f7dcd50610f024c60549def'] = 'Active the module to receive auto updates and premium support';
$_MODULE['<{layerslider}prestashop>_361ea4c6ca1bd2f1d7e88738e6440fd7'] = 'Keep the module up-to-date by activating the auto-update feature. We regularly release updates with new features, bug fixes and other improvements.';
$_MODULE['<{layerslider}prestashop>_e2790b069fb9a33fa1717f07c13ef4ee'] = 'Check out the included demo sliders';
$_MODULE['<{layerslider}prestashop>_1b9421b636e544a93e4638d45208a019'] = 'We’ve bundled several sample sliders into the module, so you can see how they built. You can find really simple sliders as well as compled and content rich ones.';
$_MODULE['<{layerslider}prestashop>_a2a02913cf1ba6823818b523282059f9'] = 'Read online resouces to get help';
$_MODULE['<{layerslider}prestashop>_322b698728e218b66646420e4616a298'] = 'We have online resouces where you can find help whether if you get stuck, experience an issue or just have a question about the item.';
