$_MODULE['<{layerslider}prestashop>_e02d2ae03de9d493df2b6b2d2813d302'] = 'Durée';
$_MODULE['<{layerslider}prestashop>_37657a348667aa7ecac5e23b51c46fc7'] = 'Ici vous pouvez régler l\'intervalle de temps entre les changements de diapositive, cette diapositive restera visible pendant le temps spécifié ici. Cette valeur est en millisecondes, donc la valeur 1000 signifie 1 seconde. N\'utilisez pas de valeurs 0 ou très faibles.';
$_MODULE['<{layerslider}prestashop>_50f4f41e03ca0aab5e91568d352c82fd'] = 'Origami';
$_MODULE['<{layerslider}prestashop>_af66fa757854d80db87737e2a4b8844b'] = 'Nous avons fait nos transitions de diapositives prédéfinies avec un soin particulier pour s\'adapter dans la plupart des cas d\'utilisation. Toutefois, si vous souhaitez augmenter ou diminuer la vitesse de ces transitions, vous pouvez les remplacer par leur temporisation en fournissant votre propre longueur de transition en millisecondes. (1 seconde = 1000 millisecondes)';
$_MODULE['<{layerslider}prestashop>_38912a644194123a9ee9d01e126fcfae'] = 'personnaliser la durée';
$_MODULE['<{layerslider}prestashop>_1605a4f4a4e45503d3ac914de9fba80a'] = 'Décalage dans le temps';
$_MODULE['<{layerslider}prestashop>_6d106138f5fadb601a337fca4d4b7a2e'] = 'Vous pouvez décaler le point de départ du calendrier de l\'animation de diapositive, afin que les calques puissent s\'animer plus tôt après un changement de diapositive. Cette valeur est en millisecondes. Une seconde est de 1000 millisecondes. Vous ne pouvez utiliser qu\'une valeur négative.';
$_MODULE['<{layerslider}prestashop>_e1f70f4a4265cb51c1b219aa60eaa441'] = 'Entrer URL';
$_MODULE['<{layerslider}prestashop>_dce5ef7bcb8f68129455e14e1c7cd9cb'] = 'If you want to link the whole slide, type the URL here. You can choose from the dropdown list when you click into this field. You can also type a hash mark followed by a number to link this layer to another slide. Example: #3 - this will switch to the third slide.';
$_MODULE['<{layerslider}prestashop>_1f7412ef1f7fdba3cc269af9a0b7a40c'] = 'Lien cible';
$_MODULE['<{layerslider}prestashop>_e82688575274fd1ba522f5044431cc93'] = 'Faites défiler jusqu\'à l\'élément (Entrer le sélecteur)';
$_MODULE['<{layerslider}prestashop>_1fc71d8275be86e2787bc4a8f0d62c84'] = 'Choisissez si le lien du slide doit se trouver en haut ou en dessous de vos layers. L\'option ultérieure rend le lien cliquable uniquement dans les espaces vides où l\'arrière-plan du slide est visible et vous permet de lier les slides et les layers indépendamment l\'une de l\'autre.';
$_MODULE['<{layerslider}prestashop>_7e04fe64ab136e31d62fcc6ea113f1ca'] = 'Sur la couche';
