$_MODULE['<{layerslider}prestashop>_06f695600f91bfbd579d9d8f5a0147bd'] = 'Vous pouvez appliquer un attribut ID sur l\'élément HTML de cette diapositive pour qu\'il fonctionne avec votre code CSS ou Javascript personnalisé.';
$_MODULE['<{layerslider}prestashop>_550dc007dba485adc22eddfe0af40a81'] = 'Deeplink';
$_MODULE['<{layerslider}prestashop>_2370ab575d5bcb31286546ba6f3a84db'] = 'Vous pouvez spécifier un nom d\'alias de diapositive que vous pouvez utiliser dans vos URL avec une marque de hachage, donc Creative Slider commencera avec la diapositive correspondant.';
$_MODULE['<{layerslider}prestashop>_436e4906cf91bb911a38eb161e858a69'] = 'Survol global';
$_MODULE['<{layerslider}prestashop>_f22bc2f96ed30d26a29060598c44c2fa'] = 'En activant cette option, toutes les layers déclencheront leurs Transitions au survol en même temps lorsque vous passerez la souris sur le slider. Il est utile de créer des effets spectaculaires qui impliquent des transitions de layers multiples et d\'activer le survol sur le slider au lieu des layers individuels.';
$_MODULE['<{layerslider}prestashop>_7acdf85c69cc3c5305456a293524386e'] = 'Caché';
$_MODULE['<{layerslider}prestashop>_e32d5c1ab7aef83217ea462806ea062c'] = 'Si vous ne souhaitez pas utiliser cette diapositive dans votre page de garde, mais que vous souhaitez la conserver, vous pouvez la masquer avec ce commutateur.';
$_MODULE['<{layerslider}prestashop>_d33fd48027b557f5bcb3f15a238d7537'] = 'Couches de<br> débordement';
$_MODULE['<{layerslider}prestashop>_5bbae9c02b64de11da4d22707a297710'] = 'Par défaut, le Slider clips les couches en dehors de ses limites. Activez cette option pour autoriser le débordement de contenu.';
$_MODULE['<{layerslider}prestashop>_4252b72e6ebcd4d4b4c2e46a786f03d2'] = 'Zoom';
$_MODULE['<{layerslider}prestashop>_f5ca4abce85e2dddb0342d0bae3a7270'] = 'Zoom entrant';
$_MODULE['<{layerslider}prestashop>_30850b501f98539b1aabaa29fabe41ef'] = 'Zoom sortant';
$_MODULE['<{layerslider}prestashop>_8d2de5368588552fbae54044ac5c7b3d'] = 'Rotation';
$_MODULE['<{layerslider}prestashop>_8aa2fa2e684ba408f551f3504c47427e'] = 'La quantité de rotation (le cas échéant) en degrés utilisée dans l\'effet Ken Burns. Les valeurs négatives sont autorisées dans le sens inverse des aiguilles d\'une montre.';
$_MODULE['<{layerslider}prestashop>_2ed8e1d37be922f23b2713c3e69a85ae'] = 'Augmenter ou diminuer la taille de l\'image de fond de la diapositive dans l\'effet Ken Burns. La valeur par défaut est 1, la valeur 2 doublera l\'image, tandis que 0,5 sera la moitié de la taille. Les valeurs négatives retournent l\'image.';
$_MODULE['<{layerslider}prestashop>_a1fa27779242b4902f7ae3bdd5c6d508'] = 'Type';
$_MODULE['<{layerslider}prestashop>_4a2d97fd5296d09fea3cbe45d107a7b3'] = 'Valeur par défaut des couches de parallaxe sur cette diapositive, qu\'elles hériteront, à moins que vous ne les définissiez autrement sur les calques concernés.';
$_MODULE['<{layerslider}prestashop>_8c94134f25efe7680f9743b2774e8e1f'] = '2D';
$_MODULE['<{layerslider}prestashop>_76aa96369abbba52e621bfa83da8e64f'] = '3D';
$_MODULE['<{layerslider}prestashop>_a4ecfc70574394990cf17bd83df499f7'] = 'Événement';
$_MODULE['<{layerslider}prestashop>_87932092d26d18e63889036bbf70518e'] = 'Vous pouvez déclencher l\'effet de parallaxe en faisant défiler la page ou en déplaçant votre curseur de la souris / inclinant votre appareil mobile. Il s\'agit de la valeur par défaut de cette diapositive, que les couches de parallaxe hériteront, à moins que vous ne les définissiez autrement directement dessus.';
$_MODULE['<{layerslider}prestashop>_58af070b9f7f3dc090af0ece708e8869'] = 'Curseur ou Inclinaison';
$_MODULE['<{layerslider}prestashop>_61ca8fa7a7c8b4699523bc8773c306d0'] = 'Axes';
$_MODULE['<{layerslider}prestashop>_fc888362a84b5b975136bbca8e1fc5c6'] = 'Choisissez sur quels axes les couches de parallaxe doivent se déplacer. Il s\'agit de la valeur par défaut de cette diapositive, que les couches de parallaxe hériteront, à moins que vous ne les définissiez autrement directement dessus.';
$_MODULE['<{layerslider}prestashop>_6adf97f83acf6453d4a6a4b1070f3754'] = 'Rien';
$_MODULE['<{layerslider}prestashop>_2c452204ed58a6f4a3bcda80344b338a'] = 'Les deux axes';
$_MODULE['<{layerslider}prestashop>_ad09aaae9c9ffe403ffdb2a6568f5a36'] = 'Horizontal uniquement';
$_MODULE['<{layerslider}prestashop>_354908b89248f3dcbab970b32860a331'] = 'Vertical uniquement';
$_MODULE['<{layerslider}prestashop>_8b622b0a6ecf4e0116eaf248876f8700'] = 'Transformer l\'origine';
$_MODULE['<{layerslider}prestashop>_e5a904ff73268b65730e4fb8b6127bf1'] = 'Définit un point sur la toile à partir duquel les transformations sont calculées. Par exemple, une couche peut tourner autour de son axe central ou un point complètement personnalisé, tel qu\'un de ses coins. Les trois valeurs représentent les axes X, Y et Z dans l\'espace 3D. En plus des valeurs de pixel et de pourcentage, vous pouvez également utiliser les constantes suivantes: haut, droite, bas, gauche, centre.';
$_MODULE['<{layerslider}prestashop>_97289d779de759c4fe3a0ef9d3a4ff3d'] = 'Durée de déplacement';
$_MODULE['<{layerslider}prestashop>_ef82e470cae5696da7dc47e9a50fa8f7'] = 'Contrôle la vitesse d\'animation des calques lorsque vous déplacez votre curseur de la souris ou inclinez votre appareil mobile. Il s\'agit de la valeur par défaut de cette diapositive, que les couches de parallaxe hériteront, à moins que vous ne les définissiez autrement directement dessus.';
$_MODULE['<{layerslider}prestashop>_0bdd8970e06688e4a6ad694b8ed7b9f7'] = 'Durée d\'attente';
$_MODULE['<{layerslider}prestashop>_9aa9b8945ad7ff74e04fb53dbbe3d1f2'] = 'Contrôle la vitesse à laquelle vos calques reviennent à leur position d\'origine lorsque vous déplacez votre curseur de souris à l\'extérieur d\'un curseur de parallaxe. Cette valeur est en millisecondes. 1 seconde = 1000 millisecondes. Il s\'agit de la valeur par défaut de cette diapositive, que les couches de parallaxe hériteront, à moins que vous ne les définissiez autrement directement dessus.';
$_MODULE['<{layerslider}prestashop>_0aa6f4210bf373c95eda00232e93cd98'] = 'Distance';
$_MODULE['<{layerslider}prestashop>_396950a091a406fcd6f9f4cc75927536'] = 'Augmenter ou diminuer la quantité de mouvement de la couche lorsque vous déplacez votre curseur de la souris ou basculez sur un appareil mobile. Il s\'agit de la valeur par défaut de cette diapositive, que les couches de parallaxe hériteront, à moins que vous ne les définissiez autrement directement dessus.';
$_MODULE['<{layerslider}prestashop>_f1a42bd417390fc63b030a519624607a'] = 'Rotation';
$_MODULE['<{layerslider}prestashop>_331ee65576c7333ba77a13d691219105'] = 'Augmenter ou diminuer la quantité de rotation de couche dans l\'espace 3D lorsque vous déplacez votre curseur de souris ou basculez sur un appareil mobile. Il s\'agit de la valeur par défaut de cette diapositive, que les couches de parallaxe hériteront, à moins que vous ne les définissiez autrement directement dessus.';
